<?php get_header(); ?><div class="main">	<?php if (have_posts()) : ?>	<?php $post = $posts[0]; // Hack. Set $post so that the_date() works. ?>  <div class="pagetitle">	<?php /* If this is a category archive */ if (is_category()) { ?>			<h2>Entries in '<?php echo single_cat_title(); ?>' </h2>	<?php /* If this is a daily archive */ } elseif (is_day()) { ?>		<h2>Archive for <?php the_time('F jS, Y'); ?></h2>	<?php /* If this is a monthly archive */ } elseif (is_month()) { ?>		<h2>Archive for <?php the_time('F, Y'); ?></h2>	<?php /* If this is a yearly archive */ } elseif (is_year()) { ?>		<h2>Archive for <?php the_time('Y'); ?></h2>	<?php /* If this is a search */ } elseif (is_search()) { ?>		<h2>Search Results</h2>	<?php /* If this is an author archive */ } elseif (is_author()) { ?>		<h2>Author Archive</h2>	<?php /* If this is a paged archive */ } elseif (isset($_GET['paged']) && !empty($_GET['paged'])) { ?>		<h2>Archives</h2>	<?php } ?>  </div>		<p><?php posts_nav_link(' &#8212; ', __('&laquo; Previous Page'), __('Next Page &raquo;')); ?></p>    	<?php while (have_posts()) : the_post(); ?>	<h4><a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title(); ?>"><?php the_title(); ?></a>	<?php edit_post_link('&nbsp;&nbsp;&nbsp;&nbsp;(edit)','<span class="editlink">','</span>'); ?>  </h4>    <div class="postcontent">	<?php the_content('<br/>Read the rest of this entry &raquo;'); ?>			<div class="entrymeta">		<div>    <span><?php the_time('M jS, Y @ g:i a') ?> CST</span>    &nbsp;&#171;|&#187;&nbsp;    <span><a href="<?php the_permalink() ?>" rel="bookmark" title='Permanent Link to "<?php the_title(); ?>"'>Permalink</a></span>    &nbsp;&#171;|&#187;&nbsp;    <a href="<?php trackback_url() ?>" title="Copy this URI to trackback this entry.">Trackback</a>     <br/>		<span>Categories: <?php the_category(' &bull; ') ?></span>    &nbsp;&#171;|&#187;&nbsp;		<?php comments_popup_link('No Comments', '1 Comment', '% Comments', 'commentslink'); ?>		</div>    <span>&nbsp;</span>    </div>  </div>		<?php endwhile; ?><br/>		<p><?php posts_nav_link(' &#8212; ', __('&laquo; Previous Page'), __('Next Page &raquo;')); ?></p>	<?php else : ?>		<h2 class="center">Not Found</h2>		<?php endif; ?>	</div><?php get_footer(); ?>